    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(tfPassword.getText() == tfConfirmPassword.getText()){
            JOptionPane.showMessageDialog(null,"Passowrd dan Konfirmasi Password Tidak Sesuai!!!","GAGAL",JOptionPane.ERROR_MESSAGE);
        }else{
            if(register(tfUsername.getText(),tfPassword.getText())){
                JOptionPane.showMessageDialog(null,"Akun Telah Dibuat, Silahkan Login Kembali!!!","BERHASIL",JOptionPane.INFORMATION_MESSAGE);
            }else{
                JOptionPane.showMessageDialog(null,"Username Telah Digunakan!!!","GAGAL",JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

