    private boolean CekDuplikatUsername(String username, String password) {
        try {
            String query = "SELECT * FROM akun WHERE Username = ? AND Password = ?";
            PreparedStatement preparedStatement = Koneksi.getConnection().prepareStatement(query);
            preparedStatement.setString(1, username);
            preparedStatement.setString(2, password);
            ResultSet resultSet = preparedStatement.executeQuery();
            if(resultSet.next()){
                return true;
            }else{
                return false;
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }

        return false;
    }
    
    private boolean register(String username, String password) {
        try {
            if (CekDuplikatUsername(username,password)){
                return false;
            }else{
                String query = "INSERT INTO akun (Username, Password) VALUES (?, ?)";
                PreparedStatement preparedStatement = Koneksi.getConnection().prepareStatement(query);
                preparedStatement.setString(1, username);
                preparedStatement.setString(2, password);
                preparedStatement.executeUpdate();
                return true;
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }

        return false;
    }

